          {movieRating.metaCritic}
        </h1>
      </div>
      <div className="rating-group">
      <img src="/img/justWatch.png" className="source"/>
      <h1 className="rating">
        {movieRating.justWatch}
      </h1>
    </div>
  </div>
    )}
  </div>

  )
}

export default App
