import React, {useEffect, useState} from 'react'
import "./App.css";

function App(){
  const [title, setTitle] = useState([]);
  const [movieRating, setMovieRating] = useState(null);

  async function handleSubmit(e){
    e.preventDefault();
    const movieTitle = e.target.title.value;
    setTitle(movieTitle);
    try{
      const response = await fetch('/ratings?title=${movieTitle}');
      const data = await response.json();
      setMovieRating(data);

    }catch(err){
      console.error(err);
      setMovieRating(data);

    }

    //Check if it has space in it and treat it
    //Call function from routes to send to backend
  }

  return(
    <div>
      <div>
        <h1 class="main-text">Type a Movie Title</h1>
      </div>
      <div class="input-form">
      <form onSubmit={(e)=> handleSubmit(e)}>
        <input 
          name="title"
          type="text"
          required
          value={title}
          onChange={(e)=> setTitle(e.target.value)}></input>
        <div class="button-container">
          <button 
            class="search-button"
            type="submit">Submit</button>
      </div>
      </form>
      </div>
  <div class="wrapper">
    {title.map(titles=>{
      return <div>
      <div class="rating-group">
        <img src="/img/imdb2.png" class="source"/>
        <h1 class="rating">
          92%
        </h1>
      </div>
      <div class="rating-group">
        <img src="/img/metaCritic.png" class="source"/>
        <h1 class="rating">
          96%
        </h1>
      </div>
    </div>
    })}

</div>
    </div>

  )
}

export default App
